function reload(a){$("#facts").load("modul/dashboard/getFacts.php",function(){if(a){$("#facts").slideDown("slow")}});$("#entries").load("modul/dashboard/getEntries.php",function(){if(a){$("#entries").slideDown("slow")}})}function delEntry(a){$.ajax({type:"POST",data:{entryID:a},url:"modul/dashboard/removeWeight.php",success:function(b){if(b){$("#errorText").html(b);$("#errorAlert").fadeIn("fast")}else{$("#successText").html("Successfully Removed.");$("#successAlert").fadeIn("fast").delay(2000).fadeOut("fast");reload()}}})}function timeStamp(){var b=new Date();var a=[b.getFullYear(),b.getMonth()+1,b.getDate()];var d=[b.getHours(),b.getMinutes(),b.getSeconds()];for(var c=1;c<3;c++){if(d[c]<10){d[c]="0"+d[c]}}return a.join("-")+" "+d.join(":")}function setTimestamp(){setTimeout(function(){$("#inputDate").val(timeStamp());setTimestamp()},1000)}$(document).ready(function(){reload(1);$("#addEntryBox").slideDown("fast");setTimestamp();$("#addEntryButton").click(function(c){$("#errorAlert").fadeOut("fast");$(this).prop("disabled",true);c.preventDefault();var b=$("#inputDate").val();var d=$("#inputWeight").val();var a="";if(b<2){a+="<li>Please enter a correct Date</li>"}if(d<8){a+="<li>Please enter a weight</li>"}if(a!=""){$("#errorText").html(a);$("#errorAlert").fadeIn("fast");$(this).prop("disabled",false)}else{$.ajax({type:"POST",data:{date:b,weight:d},url:"modul/dashboard/addWeight.php",success:function(e){if(e){$("#errorText").html(e);$("#errorAlert").fadeIn("fast");$("#addEntryButton").prop("disabled",false)}else{$("#successText").html("Successfully Added.");$("#successAlert").fadeIn("fast").delay(2000).fadeOut("fast");reload();$("#inputWeight").val("");$("#inputDate").val();$("#addEntryButton").prop("disabled",false)}}})}})});